import React, { Component } from 'react';
import { connect } from 'react-redux';



class ProductPage extends Component {
    render() {
        const data = this.props.data;
        console.log(data)
        // let data = {
        //     name: 'Войлочный ширдак',
        //     description: "lorem ipsum",
        //     options: {
        //         size: '50x50sm',
        //         structure: 'felt, cotton',
        //     },
        //     category: 'SHYRDAK',
        //     categoryName: 'Ширдаки'
        // }

        console.log(data)
        return (
            <div className="product-page">

                <h1 className='title' > {data.name} </h1>

                <div className='productImg'/>

                <div className='productContent'>
                    <div className='productDesc'>
                        <h2>  Описание  </h2>

                        <span className='productDescText'>
                            {data.description}
                        </span>
                    </div>

                    <div className='productOpt'>
                        <h2> Характристика </h2>
                        <span className='productOptText'>
                            {data.options}
                        </span>
                    </div>
                </div>

                <div className='productFooter'>
                    <button>Добавить в сундук</button>
                    <h3 className='productPrice'>   {data.price}    </h3>
                </div>
            </div>
        );
    }
}

export default connect()(ProductPage);
